import React,{Component} from 'react';
import axios from "axios";
import{
  Navbar,
  Row,
  Col,
  Form,
  Button,
  Card

}from "react-bootstrap";

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      dataApi:[],
      edit:false,
      dataPost:{
        id:0,
        title:'',
        body:''
      }
    };
    this.handleRemove=this.handleRemove.bind(this);
    this.inputChange=this.inputChange.bind(this);
    this.onSubmitForm=this.onSubmitForm.bind(this);

  }

  reloadData(){
    axios.get('http://localhost:3004/data-karyawan').then(res =>{
      this.setState({
        dataApi:res.data
      })
    });

  }


  handleRemove(e){
    console.log(e.target.value);
    fetch(`http://localhost:3004/data-karyawan/${e.target.value}`,{
      method:"DELETE"
    }).then(res => this.reloadData());

  }
  inputChange(e){
    let newdataPost={...this.state.dataPost};
    if(this.state.edit===false){
      newdataPost["id"] = new Date().getTime();
    }
    newdataPost[e.target.name] = e.target.value;

    this.setState(
      {
        dataPost: newdataPost
      },
      ()=>console.log(this.state.dataPost)
    );

  }

  clearData =()=>{
    let newdataPost = {...this.state.dataPost};

    newdataPost['id']="";
    newdataPost['body']="";
    newdataPost['title']="";

    this.setState({
      dataPost : newdataPost
    });
  }

  onSubmitForm = () =>{
    if(this.state.edit === false){
    axios.post('http://localhost:3004/data-karyawan',this.state.dataPost)
    .then(()=>{
      this.reloadData();
      this.clearData();

    })
  }else{
    axios.put(`http://localhost:3004/posts/data-karyawan/${this.state.dataPost.id}`,this.state.dataPost).then(()=>{
      this.reloadData();
      this.clearData();

    })
  }
  }

  getDataId=(e)=>{
    axios
      .get(`http://localhost:3004/posts/${e.target.value}`)
      .then(res=>{
        this.setState({
          dataPost:res.data,
          edit:true
        });
      });

  };




componentDidMount(){
  // fetch('https://jsonplaceholder.typicode.com/posts')
  // .then(response => response.json())
  // .then(res => {
  //   this.setState({
  //     dataApi: res
  //   })
  // })

}





  render(){
    return (
      <div>
        <Navbar style={{backgroundColor:"yellow"}}>
          <Navbar.Brand style={{alignText:"center"}}>
            <img
              src="https://pngimage.net/wp-content/uploads/2018/06/icon-karyawan-png-3.png"
              width="150"
              height="80"
              className="d-inline-block align-top"
              alt ="gambar"
            />

          </Navbar.Brand>
          <h1 style={{color:"white"}}>Data Karyawan</h1>
        </Navbar>
        <br />
        <Row>
        <Col xs={2}></Col>
        <Col xs={9}>
          <Form>
            <Form.Row>
              <Col>
                <Form.Control type="text" placeholder="Nama Karyawan"  value={this.state.dataPost.nama_karyawan}/>
              </Col>
              <Col>
                <Form.Control type="text" placeholder="Pekerjaan" value={this.state.dataPost.jabatan} />
              </Col>

              <Col>
                <Form.Control type="text" placeholder="Jenis Kelamin" value={this.state.dataPost.jenis_kelamin} />
              </Col>

              <Col>
                <Form.Control type="date" value={this.state.dataPost.tanggal_lahir} />
              </Col>

              <Col>
                <Button style={{paddingleft:"20px"}} variant="primary" type='submit' onClick={this.onSubmitForm}>Save Data</Button>
              </Col>
            </Form.Row>
          </Form>
          <br />
          <Row>
          <Col xs={2}></Col>
          <Col xs={6}>
            {this.state.dataApi.map((dat,index)=>
            {
                return(
                  <div key={index}>

                    <p>
                      <Card border="info" text="black" style={{width:'500px'}}>
                      <Card.Body>
                        <Card.Text>
                          Nama : {dat.nama_karyawan}
                          <br/><br/>
                          Jabatan : {dat.jabatan}
                          <br/><br/>
                          Jenis Kelamin : {dat.jenis_kelamin}
                          <br/><br/>
                          Tanggal Lahir :{dat.tanggal_lahir}
                          <br/><br/>
                          <Button value={dat.id} onClick={this.handleRemove} variant="danger">DELETE</Button>
                          <Button variant="success">EDIT DATA</Button>
                        </Card.Text>
                      </Card.Body>
                      </Card>
                    </p>

                  </div>);

            })}
            <br />
          </Col>
          <Col></Col>
          </Row>
        </Col>
        <Col></Col>
        </Row>

      </div>
    );
  }
}

export default App;
