import React,{Component} from 'react';
import axios from "axios";
import{
  Navbar,
  Row,
  Col,
  Form,
  Button,
  Card

}from "react-bootstrap";

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      dataApi:[]
    }
  }

componentDidMount(){
  // fetch('https://jsonplaceholder.typicode.com/posts')
  // .then(response => response.json())
  // .then(res => {
  //   this.setState({
  //     dataApi: res
  //   })
  // })
  axios.get('https://jsonplaceholder.typicode.com/posts').then(res =>{
    this.setState({
      dataApi:res.data
    })
  });
}





  render(){
    return (
      <div>
        <Navbar style={{backgroundColor:"yellow"}}>
          <Navbar.Brand style={{alignText:"center"}}>
            <img
              src="https://pngimage.net/wp-content/uploads/2018/06/icon-karyawan-png-3.png"
              width="150"
              height="80"
              className="d-inline-block align-top"
              alt ="gambar"
            />

          </Navbar.Brand>
          <h1 style={{color:"white"}}>Data Karyawan</h1>
        </Navbar>
        <br />
        <Row>
        <Col xs={2}></Col>
        <Col xs={9}>
          <Form>
            <Form.Row>
              <Col>
                <Form.Control type="text" placeholder="Nama Karyawan" />
              </Col>
              <Col>
                <Form.Control type="text" placeholder="Pekerjaan" />
              </Col>

              <Col>
                <Form.Control type="text" placeholder="Jenis Kelamin" />
              </Col>

              <Col>
                <Form.Control type="date"  />
              </Col>

              <Col>
                <Button style={{paddingleft:"20px"}} variant="primary">Save Data</Button>
              </Col>
            </Form.Row>
          </Form>
          <br />
          <Row>
          <Col xs={2}></Col>
          <Col xs={6}>
            {this.state.dataApi.map((dat,index)=>
            {
                return(
                  <div key={index}>

                    <p>
                      <Card border="info" text="black" style={{width:'500px'}}>
                      <Card.Body>
                        <Card.Text>
                          {dat.title}
                          <br/><br/>
                          <button>DELETE</button>
                        </Card.Text>
                      </Card.Body>
                      </Card>
                    </p>

                  </div>);

            })}
            <br />
          </Col>
          <Col></Col>
          </Row>
        </Col>
        <Col></Col>
        </Row>

      </div>
    );
  }
}

export default App;
